package com.syntax.class08;

public class Task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i=1; i<=100; i++) {
			System.out.print(i+" ");
		}
		System.out.println();
		for (int i=20; i>=1; i--) {
			System.out.print(i+ " ");
		}
		System.out.println();
		for (int m=20; m>=1; m--) {
			if(m% 2== 0) {
			System.out.print(m+ " ");
			}
	}
	}
}
